$(function() {
    Common.init();
    $(".fancybox").fancybox({
        helpers: {
            overlay: {
                locked: !1
            }
        }
    })
});
var Common = {
    init: function() {
        new Common.MobileCSS;
        new Common.LogoAnime;
        new Common.CreateMap;
        new Common.NavCurrent;
        new Common.ImageHoverChange;
        new Common.ImageHoverOpacity
    }
};
Common.MobileCSS = function() {
    var e = navigator.userAgent;
    if (e.indexOf("iPhone") > 0 || e.indexOf("iPad") > 0 || e.indexOf("iPod") > 0 || e.indexOf("Android") > 0) {
        $(".cd-fixed-bg").css({
            "background-attachment": "scroll"
        });
        $("[data-tel]").each(function() {
            var e = $(this).attr("data-tel");
            $(this).wrap("<a href=tel:" + e + "></a>")
        })
    }
};
Common.LogoAnime = function() {
    $("#Logo").transition({
        opacity: 0,
        scale: 1.1
    });
    $(window).load(function() {
        $("#Logo").show().transition({
            opacity: 1,
            scale: 1
        }, 700, "easeInOutQuad")
    })
};
Common.CreateMap = function() {
    if ($("body").hasClass("home")) {
        var e = new google.maps.LatLng(34.690313416124, 135.5134435944774), t = {
            zoom: 16,
            center: e,
            mapTypeId: google.maps.MapTypeId.ROADMAP,
            disableDefaultUI: !0,
            scrollwheel: !1
        }, n = new google.maps.Map(document.getElementById("MapCanvas"), t), r = [{
            stylers: [{
                hue: "#F5F5F5"
            }, {
                gamma: 1.5
            }, {
                saturation: -100
            }
            ]
        }
        ], i = new google.maps.StyledMapType(r);
        n.mapTypes.set("noText", i);
        n.setMapTypeId("noText");
        var s = {
            position: e,
            map: n,
            title: ""
        }, o = new google.maps.Marker(s)
    }
};
Common.NavCurrent = function() {
    function r(e) {
        if (e != t) {
            t = e;
            secNum2 = e + 1;
            $("#Header li a").removeClass("cu");
            $("#Header li:nth-child(" + secNum2 + ") a").addClass("cu")
        }
    }
    function i() {
        st = $(window).scrollTop() + 60;
        for (var t = e.length-1; t >= 0; t--)
            if (st > e[t]) {
                r(t);
                break
            }
    }
    var e = new Array, t =- 1, n = window.innerHeight ? window.innerHeight : $(window).height();
    $(".cd-scrolling-bg").each(function(t) {
        e[t] = $(this).offset().top
    });
    $(window).on("load scroll", i);
    $("#Header li a").click(function() {
        if (!$(this).hasClass("blog")) {
            $(window).off("load scroll", i);
            $("#Header li a").removeClass("cu");
            $(this).addClass("cu");
            var e = $(this).attr("href"), t = $(e).offset().top-50;
            $("html,body").stop().animate({
                scrollTop: t
            }, 1e3, "easeInOutExpo", function() {
                $(window).on("load scroll", i)
            });
            return !1
        }
    })
};
Common.ImageHoverChange = function() {
    if (jQuery.support.opacity) {
        var e = 200, t = $("a img");
        t.each(function() {
            if (this.src.match("_off")) {
                var t = $(this).width(), n = $(this).height();
                $(this).parent("a").css({
                    display: "inline-block",
                    width: t,
                    height: n
                });
                this.onImgSrc = new Image;
                this.onImgSrc.src = this.getAttribute("src").replace("_off", "_on");
                $(this.onImgSrc).css({
                    position: "absolute",
                    opacity: 0
                });
                $(this).before(this.onImgSrc);
                $(this.onImgSrc).mousedown(function() {
                    $(this).stop().animate({
                        opacity: 0
                    }, e, "easeInOutQuad")
                });
                $(this.onImgSrc).hover(function() {
                    $(this).stop().animate({
                        opacity: 1
                    }, e, "easeInOutQuad")
                }, function() {
                    $(this).stop().animate({
                        opacity: 0
                    }, e, "easeInOutQuad")
                })
            }
        })
    } else 
        $('a[href] img, input[type="image"]').mouseover(function() {
            $(this).attr("src", $(this).attr("src").replace("_off", "_on"));
            $(this).mouseout(function() {
                $(this).attr("src", $(this).attr("src").replace("_on", "_off"))
            })
        })
};
Common.ImageHoverOpacity = function() {
    var e = 200;
    $("#Header li a img,.ov").hover(function() {
        $(this).stop().fadeTo(e, .3)
    }, function() {
        $(this).stop().fadeTo(e, 1)
    })
};
